import React, { useState } from "react";
import Confetti from "react-confetti";
import { useNavigate } from "react-router-dom";

const RaffleShow = () => {
  const initialPrizes = [
  {
    name: "iPhone 15 Pro",
    price: "$1,299",
    image:
      "https://fdn2.gsmarena.com/vv/pics/apple/apple-iphone-15-pro-1.jpg",
  },
  {
    name: "MacBook Air M3",
    price: "$1,199",
    image:
      "https://th.bing.com/th/id/OPAC.f0oA28OUsywifw474C474?w=199&h=150&rs=1&o=5&dpr=1.5&pid=21.1",
  },
  {
    name: "55-inch Television",
    price: "$799",
    image:
      "data:image/webp;base64,",
  },
  {
    name: "PS5 Console",
    price: "$499",
    image:
      "https://th.bing.com/th/id/OIP.J3EZNtyvsD7jjA-bNgp7VwHaHa?rs=1&pid=ImgDetMain",
  },
  {
    name: "Electric Scooter",
    price: "$999",
    image:
      "data:image/webp;base64,",
  },
  {
    name: "Smartwatch",
    price: "$299",
    image:
      "https://m.media-amazon.com/images/I/61Qe0euJJZL._AC_SL1500_.jpg",
  },
  {
    name: "Noise Cancelling Headphones",
    price: "$349",
    image:
      "https://i.gadgets360cdn.com/products/large/1-800x1004-1668527939.jpg",
  },
  {
    name: "Amazon Gift Card",
    price: "$100",
    image:
      "data:image/webp;base64,",
  },
];


  const [prizes, setPrizes] = useState(initialPrizes);
  const [finalPrize, setFinalPrize] = useState(null);
  const [running, setRunning] = useState(false);
  const  navigate = useNavigate();

  const startRaffle = () => {
    if (running || finalPrize) return;
    setRunning(true);
    const removeInterval = setInterval(() => {
      setPrizes((prev) => {
        if (prev.length <= 1) {
          clearInterval(removeInterval);
          setFinalPrize(prev[0]);
          setRunning(false);
          return prev;
        }
        const index = Math.floor(Math.random() * prev.length);
        return prev.filter((_, i) => i !== index);
      });
    }, 800);
  };

  const resetRaffle = () => {
    setPrizes(initialPrizes);
    setFinalPrize(null);
    setRunning(false);
  };

  return (
    <div className="min-h-screen bg-neutral-100 p-6 flex flex-col items-center justify-center gap-6">
      {!running && !finalPrize && (
        <button
          onClick={startRaffle}
          className="bg-yellow-400 hover:bg-yellow-300 text-lg font-bold px-6 py-3 rounded shadow"
        >
          Start Raffle
        </button>
      )}

      <div className="grid grid-cols-2 gap-4 w-full max-w-3xl">
        {prizes.map((item, i) => (
          <div
            key={i}
            className="bg-white rounded-2xl shadow-lg p-4 flex items-center gap-4 transition transform hover:scale-105 duration-300"
          >
            <img
              src={item.image}
              alt={item.name}
              className="w-16 h-16 object-cover rounded-lg"
            />
            <div className="text-left">
              <p className="text-lg font-bold">{item.name}</p>
              <p className="text-sm text-gray-500">{item.price}</p>
            </div>
          </div>
        ))}
      </div>

      {finalPrize && (
        <>
          <Confetti numberOfPieces={500} recycle={false} />
          <div className="fixed inset-0 bg-white bg-opacity-95 flex flex-col items-center justify-center z-50 p-4">
            <h1 className="text-4xl font-bold mb-4 text-center">
              ðŸŽ‰ Winner Prize: {finalPrize.name} ðŸŽ‰
            </h1>
            <img
              src={finalPrize.image}
              alt={finalPrize.name}
              className="w-40 h-40 object-cover rounded-xl mb-4"
            />
            <p className="text-2xl text-gray-700 font-semibold mb-4">
              {finalPrize.price}
            </p>
            <button
              onClick={()=>navigate("/")}
              className="bg-blue-500 hover:bg-blue-400 text-white px-5 py-2 rounded shadow"
            >
              Close
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default RaffleShow;
